import { useSelector } from "react-redux";


function PrivateRoute({ children, ...rest }) {
        let auth = useSelector(state=>state.auth.authed);
        return (
          <Route
            {...rest}
            render={({ location }) =>
              auth.user ? (
                children
              ) : (
                <Redirect
                  to={{
                    pathname: "/login",
                    state: { from: location }
                  }}
                />
              )
            }
          />
        );
      }
