import { configureStore } from '@reduxjs/toolkit'

const initialState = {
        firstDay: null,
        lastDay: null,
        days: [],
        events: [],
}

const calendarSlice = createSlice({
        name: 'calendar',
        initialState,
        reducers: {
                dataLoaded(state,action){
                        const {firstDay,lastDay,days,events} = action.payload
                        state = {firstDay,lastDay,days,events}
                }
        }
})

export default calendarSlice.reducer
