import { createSlice } from "@reduxjs/toolkit";
import {
  weekContaining,
  setTimeDayStart,
  setTimeDayEnd,
} from "../../utilities/dateUtilities";
import { addDays } from "date-fns";
import { fetchDays } from "../../Model/Jobs";

export function getWeek(date, increment = 0) {
  const currentDate = addDays(date, increment * 7);
  const days = weekContaining(currentDate);
  const firstDay = setTimeDayStart(new Date(days[0])); //TODO
  const lastDay = setTimeDayEnd(new Date(days[days.length - 1])); //TODO
  return {
    currentDate,
    days,
    firstDay,
    lastDay,
  };
}

const initialState = {
  ...getWeek(new Date()),
  events: [],
};

const calendarSlice = createSlice({
  name: "calendar",
  initialState,
  reducers: {
    dataLoaded(state, action) {
      state.events = action.payload;
    },
  },
});

const { dataLoaded } = calendarSlice.actions;

export const fetchData = (state, dispatch) => {
  fetchDays(state.firstDay, state.lastDay)
    .then((data) => {
      console.log(data);
      dispatch(dataLoaded([...data])); //TODO have a look at what we are doing here what if there is no data
    })
    .catch(console.error);
};

export default calendarSlice.reducer;
