import { createSlice } from "@reduxjs/toolkit";

const intitialState ={
        authed: false
}

const authSlice = createSlice({
        name: "auth",
        intitialState,
        reducers: {
                logIn(state,action){
                        state.authed = true
                },
                logOut(state,action){
                        state.authed = false
                }
        }
})

export default authSlice.reducer
